import React, { useState } from 'react';
import './card.scss';

function ImageContainer({cardContent}) {
  const [srcIndex, setSrcIndex] = useState(0);

  function handleArrowClick(arrowDirection, imgSrcArray) {
    if ((arrowDirection === "left") && srcIndex === 0) {
      setSrcIndex(imgSrcArray.length - 1);
    } else if (arrowDirection === "left") {
      setSrcIndex(srcIndex - 1);
    } else if ((arrowDirection === "right") && (srcIndex === imgSrcArray.length - 1)) {
      setSrcIndex(0);
    } else {
      setSrcIndex(srcIndex + 1);
    }
  }

  return(
    <div className="image-wrapper">
      {cardContent.imgSrcArray.length > 1 &&
        <button className="left-arrow" onClick={() => handleArrowClick("left", cardContent.imgSrcArray)}>
            <div className="left-arrow-mask"></div>
          <img className="arrow-icon" src={require("../../assets/left-arrow.svg")} alt="left-arrow"/>
        </button>
      }
      <button className="image-container">
        <img src={require(`../../assets/${cardContent.imgSrcArray[srcIndex]}`)} alt={cardContent.alt}/>
      </button>
      {cardContent.imgSrcArray.length > 1 &&
        <button className="right-arrow" onClick={() => handleArrowClick("right", cardContent.imgSrcArray)}>
            <div className="right-arrow-mask"></div>
          <img className="arrow-icon" src={require("../../assets/right-arrow.svg")} alt="right-arrow"/>
        </button>
      }
    </div>
  );
}

function Card({cardContent}) {
  return (
    <div className="card">
      {cardContent.title &&
        <div className="item">
          <span className="label">Title:</span>
          <span>{cardContent.title}</span>
        </div>
      }
      {cardContent.compilation &&
        <div className="item">
          <span className="label">Compilation:</span>
          <span>{cardContent.compilation}</span>
        </div>
      }
      {cardContent.description &&
        <div className="item">
          <span className="label">Description:</span>
          <span>{cardContent.description}</span>
        </div>
      }
      {cardContent.media &&
        <div className="item">
          <span className="label">Media:</span>
          <span>{cardContent.media}</span>
        </div>
      }
      {cardContent.imgSrcArray &&
        <ImageContainer cardContent={cardContent}/>
      }
      {cardContent.content &&
        <div>
          {cardContent.content}

          <svg class="svg">
            <g class="svg-monkey">
              <circle cx="75" cy="120" r="60" fill="#F8DBBE"/>
              <circle cx="330" cy="120" r="60" fill="#F8DBBE"/>
              <circle cx="200" cy="160" r="140" fill="#5B2E01"/>
              <circle cx="150" cy="130" r="40" fill="#FFFFFF" stroke = "#000000" stroke-width="4"/>
              <circle cx="250" cy="130" r="40" fill="#FFFFFF" stroke = "#000000" stroke-width="4"/>
              <circle id="left-pupil" cx="150" cy="130" r="20" fill="#000000"/>
              <circle id="right-pupil" cx="250" cy="130" r="20" fill="#000000"/>
              <ellipse cx="200" cy="220" rx="150" ry="80" fill="#F8DBBE" />
              <rect x="100" y="220" width="200" height="8" fill="#E4B17E"/>
            </g>
            <g class="svg-circle">
              <circle cx="160" cy="500" r="140" fill="#FF0000"/>
              <circle cx="160" cy="500" r="120" fill ="#FF9900"/>
              <circle cx="160" cy="500" r="100" fill="#FFFF00"/>
              <circle cx="160" cy="500" r="80" fill="#00FF00"/>
              <circle cx="160" cy="500" r="60" fill="#0000FF"/>
              <circle cx="160" cy="500" r="40" fill="#660066"/>
              <circle cx="160" cy="500" r="20" fill="#9900FF"/>
            </g>
            <g class="svg-rectangle">
              <rect x="20" y="675" width="50" height="250" fill="#FF0000"/>
              <rect x="60" y="675" width="50" height="250" fill="#FF9900"/>
              <rect x="100" y="675" width="50" height="250" fill="#FFFF00"/>
              <rect x="140" y="675" width="50" height="250" fill="#00FF00"/>
              <rect x="180" y="675" width="50" height="250" fill="#0000FF"/>
              <rect x="220" y="675" width="50" height="250" fill="#660066"/>
              <rect x="260" y="675" width="50" height="250" fill="#9900FF"/>
            </g>
          </svg>

        </div>
      }
    </div>
  );
}

export default Card;
