import React, { useState } from 'react';
import './card.scss';

const [index, setIndex] = useState(0);

// function handleLeftClick() {
//   console.log("left click");
// }
//
function handleRightClick(text) {
  // if (index <=)
  // setIndex(index + 1)
  console.log(text);
}

function Image({cardContent}) {
  return(
    <div className="image-wrapper">
      <button className="left-arrow" onClick={handleLeftClick("test")}>
        {cardContent.imgSrc.length > 1 &&
          <div className="left-arrow-mask"></div>
        }
        <img className="arrow-icon" src={require("../../assets/left-arrow.svg")} alt="left-arrow"/>
      </button>
      <button className="image-container">
        <img src={require(`../../assets/${cardContent.imgSrc[0]}`)} alt={cardContent.alt}/>
      </button>
      <button className="right-arrow" onClick={handleRightClick}>
        {cardContent.imgSrc.length > 1 &&
          <div className="right-arrow-mask"></div>
        }
        <img className="arrow-icon" src={require("../../assets/right-arrow.svg")} alt="right-arrow"/>
      </button>
    </div>
  )
}

function Card({cardContent}) {
  return (
    <div className="card">
      {cardContent.title &&
        <div className="item">
          <span className="label">Title:</span>
          <span>{cardContent.title}</span>
        </div>
      }
      {cardContent.compilation &&
        <div className="item">
          <span className="label">Compilation:</span>
          <span>{cardContent.compilation}</span>
        </div>
      }
      {cardContent.description &&
        <div className="item">
          <span className="label">Description:</span>
          <span>{cardContent.description}</span>
        </div>
      }
      {cardContent.media &&
        <div className="item">
          <span className="label">Media:</span>
          <span>{cardContent.media}</span>
        </div>
      }
      <Image cardContent={cardContent}/>
    </div>
  );
}

export default Card;
