import React from 'react';
import './card.scss';

function Card({cardContent}) {
  return (
    <button className={cardContent.className}>
      {cardContent.title &&
        <p className="label">{cardContent.title}</p>
      }
      {cardContent.compilation &&
        <p className="label">{cardContent.compilation}</p>
      }
      {cardContent.description &&
        <p>{cardContent.description}</p>
      }
      <img src={require(`../../assets/${cardContent.imgSrc}`)} alt={cardContent.alt}/>
    </button>
  );
}

export default Card;
