  constructor(props) {
    super(props);
    this.state = {page: 'home'};
  }

  updateContent(pageType) {
    this.setState({page: pageType});
  }

  render() {
    return(
      [
        <Header updateContent={updateContent} />,
        <Content page={page} />,
        <Footer />
      ]
    );
  }
}

export default App;
